'''Created on 19 feb 2018@author: Serena Sensini'''from builtins import objectclass POTTERY(object):    #def __init__"    def __init__(self,                id_rep,                 divelog_id,                site,                date_,                artefact_id,                photographed,                drawing,                retrieved,                inclusions,                percent_inclusion,                specific_part,                form,                typology,                provenance,                munsell_clay,                surf_treatment,                conservation,                depth,                storage_,                period,                state,                samples,                washed,                dm,                dr,                db,                 th,                ph,                bh,                thickmin,                thickmax,                years,                box,                biblio,                description,                area,                munsell_surf,                category,                wheel_made,                ):        self.id_rep= id_rep        self.divelog_id=divelog_id        self.sito=site        self.data_=date_        self.artefact_id=artefact_id        self.photographed=photographed        self.drawing=drawing        self.retrieved=retrieved        self.fabric=inclusions        self.percent=percent_inclusion        self.specific_part=specific_part        self.specific_shape=form        self.typology=typology        self.provenience=provenance        self.munsell=munsell_clay        self.surf_trat=surf_treatment                self.treatment=conservation        self.depth=depth        self.storage_=storage_        self.period=period        self.state=state        self.samples=samples        self.washed=washed        self.dm =dm        self.dr=dr        self.db=db              self.th=th        self.ph=ph        self.bh=bh        self.thickmin=thickmin        self.thickmax=thickmax        self.anno=years        self.box=box        self.biblio=biblio        self.description=description        self.area=area        self.munsell_surf=munsell_surf        self.category=category        self.wheel_made=wheel_made    def __repr__(self):        return "<POTTERY('%d','%d','%s','%s','%s',%s','%s','%s','%s', '%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%d','%d','%s','%s','%s','%s','%s','%s')>" % (        self.id_rep,        self.divelog_id,        self.sito,        self.data_,        self.artefact_id,        self.photographed,        self.drawing,        self.retrieved,        self.fabric,        self.percent,        self.specific_part,        self.specific_shape,        self.typology,        self.provenience,        self.munsell,        self.surf_trat,                self.treatment,        self.depth,        self.storage_,        self.period,        self.state,        self.samples,        self.washed,        self.dm,        self.dr,        self.db,                self.th,        self.ph,        self.bh,        self.thickmin,        self.thickmax,        self.anno,        self.box,        self.biblio,        self.description,        self.area,        self.munsell_surf,        self.category,        self.wheel_made        )